#include "JoystickDriver.c"

void runAllMotors(int speed);
void stopAllMotors(void);
void runAllRight( int speed );
void rotate(int speed);
int value = 0;


void initializeRobot()
{
	return;
}
task main()
{

	initializeRobot();
	waitForStart();
  runAllMotors(-100);
  wait1Msec(1650);
  stopAllMotors();
  wait1Msec(100);
  rotate(-100);
  wait1Msec(600);
  if (SensorValue[IR] == 5 )
  {
  	value = 3;
  	rotate(100);
    wait1Msec(350);
  }
  else
  {
  rotate(100);
  wait1Msec(350);
  runAllMotors(100);
  while(SensorValue[IR] != 5 )
  {
  	wait1Msec(10);
  	value ++;
  }
  }
 	stopAllMotors();
 	wait1Msec(500);

 	if(value <= 40) //position 3
 	{

	rotate(100);
	wait1Msec(250);
  runAllMotors(-100);
  wait1Msec(1100);
  runAllRight(-100);
  wait1Msec(1950);
  runAllMotors(-100);
  wait1Msec(200);
  runAllRight(-100);
  wait1Msec(1000);
  runAllMotors(100);
	wait1Msec(1000);
	runAllRight(127);
	wait1Msec(2300);
 	}
 	else if( value > 20 && value <= 160) //position 2
	{
	runAllMotors(-100);
	wait1Msec(600);
	stopAllMotors();
	wait1Msec(300);
	runAllRight(-100);
	wait1Msec(610);
	stopAllMotors();
	wait1Msec(1000);
	runAllMotors(-100);
	wait1Msec(500);
	stopAllMotors();
	wait1Msec(1000);
  runAllRight(-100);
  wait1Msec(1400);
  runAllMotors(100);
  wait1Msec(720);
  runAllRight(127);
  wait1Msec(1900);
	}
	else if( value > 125 && value <= 200) //position 1
	{
	runAllMotors(100);
	wait1Msec(400);
	rotate(-100);
	wait1Msec(300);
	runAllMotors(100);
	wait1Msec(470);
	stopAllMotors();
	wait1Msec(1000);
	runAllRight(-100);
	wait1Msec(0);
	stopAllMotors();
	wait1Msec(1000);
	runAllMotors(-100);
	wait1Msec(700);
	stopAllMotors();
	wait1Msec(1000);
  runAllRight(-100);
  wait1Msec(1910);
  runAllMotors(100);
  wait1Msec(800);
  runAllRight(126);
  wait1Msec(2000);

}
} // End Task-Main

