void runAllMotors(int speed)
{
	if (speed > 127)
		speed = 127;
	else if( speed < -127)
		speed = -127;

		motor[omnitr] = speed;
		motor[omnibr] = -speed;
		motor[omnibl] = -speed;
		motor[omnitl] = speed;
}


void stopAllMotors()
{
	runAllMotors(0);
}

void runAllRight( int speed ) // Horizontal DIR
{
	motor[omnitr] = speed;
	motor[omnibr] = speed;
	motor[omnitl] = -speed;
	motor[omnibl] = -speed;

}

void rotate( int speed )
{
	motor[omnitr] = speed;
	motor[omnitl] = speed;
	motor[omnibr] = speed;
	motor[omnibl] = speed;
}
