#pragma config(Hubs,  S1, HTMotor,  HTMotor,  none,     none)
#pragma config(Sensor, S1,     ,               sensorI2CMuxController)
#pragma config(Motor,  mtr_S1_C1_1,     omnitr,        tmotorTetrix, openLoop, reversed)
#pragma config(Motor,  mtr_S1_C1_2,     omnitl,        tmotorTetrix, openLoop)
#pragma config(Motor,  mtr_S1_C2_1,     omnibr,        tmotorTetrix, openLoop)
#pragma config(Motor,  mtr_S1_C2_2,     omnibl,        tmotorTetrix, openLoop, reversed)
