#include "joystickdriver.c"
task main()
{
while(true)
{
	if (joystick.joy1_y1 > -5 && joystick.joy1_y1 < 5)
	{
		motor[omnitl] = 0;
	  motor[omnibl] = 0;
	}
	else
	{
		motor[omnitl] = joystick.joy1_y1;
		motor[omnibl] = joystick.joy1_y1;
	}
	if (joystick.joy1_y2 > -5 && joystick.joy1_y2 < 5)
	{
		motor[omnitr] = 0;
		motor[omnibr] = 0;
	}
	else
	{
		motor[omnitr] = joystick.joy1_y2;
		motor[omnibr] = joystick.joy1_y2;
	}




}


}
